package config;import dao.MemberDao;import org.apache.tomcat.jdbc.pool.DataSource;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.transaction.annotation.EnableTransactionManagement;import service.AuthService;import service.ChangePasswordService;import service.MemberRegisterService;@Configuration@EnableTransactionManagementpublic class MemberConfig {    @Bean(destroyMethod = "close")    public DataSource dataSource() {        DataSource dataSource = new DataSource();        dataSource.setDriverClassName("com.mysql.cj.jdbc.Driver");        dataSource.setUrl("jdbc:mysql://localhost/spring5fs?characterEncoding=utf8");        dataSource.setUsername("spring5");        dataSource.setPassword("spring5");        dataSource.setInitialSize(2);        dataSource.setMaxActive(10);        dataSource.setTestWhileIdle(true);        dataSource.setMinEvictableIdleTimeMillis(60000 * 3);        dataSource.setTimeBetweenEvictionRunsMillis(10 * 1000);        return dataSource;    }    @Bean    public MemberDao memberDao() {        return new MemberDao(dataSource());    }    @Bean    public MemberRegisterService memberRegisterService() {        return new MemberRegisterService(memberDao());    }    @Bean    public ChangePasswordService changePasswordService() {        ChangePasswordService changePasswordService = new ChangePasswordService();        changePasswordService.setMemberDao(memberDao());        return changePasswordService;    }    @Bean    public AuthService authService() {        AuthService authService = new AuthService();        authService.setMemberDao(memberDao());        return authService;    }}