package learningspring.chap03.config;import learningspring.chap03.dao.MemberDao;import learningspring.chap03.printer.MemberInfoPrinter;import learningspring.chap03.printer.MemberListPrinter;import learningspring.chap03.printer.MemberPrinter;import learningspring.chap03.printer.VersionPrinter;import learningspring.chap03.service.ChangePasswordService;import learningspring.chap03.service.MemberRegisterService;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;@Configurationpublic class AppCtx {    @Bean    public MemberDao memberDao() {        return new MemberDao();    }    @Bean    MemberRegisterService memberRegisterService() {        return new MemberRegisterService(memberDao());    }    @Bean    public ChangePasswordService changePasswordService() {        ChangePasswordService changePasswordService = new ChangePasswordService();        changePasswordService.setMemberDao(memberDao());        return changePasswordService;    }    @Bean    public MemberPrinter memberPrinter() {        return new MemberPrinter();    }    @Bean    public MemberListPrinter memberListPrinter() {        return new MemberListPrinter(memberDao(), memberPrinter());    }    @Bean    public MemberInfoPrinter memberInfoPrinter() {        MemberInfoPrinter memberInfoPrinter = new MemberInfoPrinter();        memberInfoPrinter.setMemberDao(memberDao());        memberInfoPrinter.setMemberPrinter(memberPrinter());        return memberInfoPrinter;    }    @Bean    public VersionPrinter versionPrinter() {        VersionPrinter versionPrinter = new VersionPrinter();        versionPrinter.setMajorVersion(5);        versionPrinter.setMinorVersion(0);        return versionPrinter;    }}