package learningspring.chap05.printer;import learningspring.chap05.dao.MemberDao;import learningspring.chap05.domain.Member;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.beans.factory.annotation.Qualifier;import org.springframework.stereotype.Component;@Component("infoPrinter")public class MemberInfoPrinter {    private MemberDao memberDao;    private MemberPrinter memberPrinter;    public MemberInfoPrinter() {    }    public void printMemberInfo(String email) {        Member member = memberDao.selectByEmail(email);        if (member == null) {            System.out.println("데이터 없음");            return;        }        memberPrinter.print(member);        System.out.println();    }    @Autowired    public void setMemberDao(MemberDao memberDao) {        this.memberDao = memberDao;    }    @Autowired    @Qualifier("printer")    public void setMemberPrinter(MemberPrinter memberPrinter) {        this.memberPrinter = memberPrinter;    }}