package learningspring.chap05.service;import learningspring.chap05.dao.MemberDao;import learningspring.chap05.domain.Member;import learningspring.chap05.dto.RegisterRequest;import learningspring.chap05.exception.DuplicateMemberException;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Component;import java.time.LocalDateTime;@Componentpublic class MemberRegisterService {    @Autowired    private MemberDao memberDao;    public MemberRegisterService() {    }    public MemberRegisterService(MemberDao memberDao) {        this.memberDao = memberDao;    }    public Long regist(RegisterRequest request) {        Member member = memberDao.selectByEmail(request.getEmail());        if (member != null) {            throw new DuplicateMemberException("dup email" + request.getEmail());        }        Member newMember = new Member(                request.getEmail(),                request.getPassword(),                request.getName(),                LocalDateTime.now());        memberDao.insert(newMember);        return newMember.getId();    }}