package learningspring.chap06.service;import learningspring.chap06.dao.MemberDao;import learningspring.chap06.domain.Member;import learningspring.chap06.exception.MemberNotFoundException;public class ChangePasswordService {    private MemberDao memberDao;    public void changePassword(String email, String oldPwd, String newPwd) {        Member member = memberDao.selectByEmail(email);        if (member == null) {            throw new MemberNotFoundException();        }        member.changePassword(oldPwd, newPwd);        memberDao.update(member);    }    public void setMemberDao(MemberDao memberDao) {        this.memberDao = memberDao;    }}