package learningspring.chap08.main;import learningspring.chap03.exception.MemberNotFoundException;import learningspring.chap08.config.AppCtx;import learningspring.chap08.exception.WrongIdPasswordException;import learningspring.chap08.service.ChangePasswordService;import org.springframework.context.annotation.AnnotationConfigApplicationContext;public class MainForCPS {    public static void main(String[] args) {        AnnotationConfigApplicationContext ctx = new AnnotationConfigApplicationContext(AppCtx.class);        ChangePasswordService changePasswordService = ctx.getBean(ChangePasswordService.class);        try {            changePasswordService.changePassword("skud113@naver.com", "1234", "1111");            System.out.println("암호를 변경했습니다.");        } catch (MemberNotFoundException e) {            System.out.println("회원 데이터가 존재하지 않습니다.");        } catch (WrongIdPasswordException e) {            System.out.println("암호가 올바르지 않습니다");        }        ctx.close();    }}