package learningspring.chap08.service;import learningspring.chap08.dao.MemberDao;import learningspring.chap08.domain.Member;import learningspring.chap08.exception.MemberNotFoundException;import org.springframework.transaction.annotation.Transactional;public class ChangePasswordService {    private MemberDao memberDao;    @Transactional    public void changePassword(String email, String oldPwd, String newPwd) {        Member member = memberDao.selectByEmail(email);        if (member == null) {            throw new MemberNotFoundException();        }        member.changePassword(oldPwd, newPwd);        memberDao.update(member);    }    public void setMemberDao(MemberDao memberDao) {        this.memberDao = memberDao;    }}