package learningspring.config;import learningspring.aspect.CacheAspect;import learningspring.aspect.ExeTimeAspect;import learningspring.chap07.Calculator;import learningspring.chap07.RecCalculator;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.context.annotation.EnableAspectJAutoProxy;@Configuration@EnableAspectJAutoProxypublic class AppCtx {    @Bean    public ExeTimeAspect exeTimeAspect() {        return new ExeTimeAspect();    }    @Bean    public CacheAspect cacheAspect() {        return new CacheAspect();    }    @Bean    public Calculator calculator() {        return new RecCalculator();    }}